import commentService from "@/services/comment/comment"


export default {
    async fetchCategory({ commit }) {
        const res = await commentService.getAll()
        commit("GET_CATEGORY", res.data)
        return res.data
    },
    async createCategory({ commit }, data) {
        const res = await commentService.create(data)
        commit("POST_CATEGORY", res.data)
        console.log(res.status)
        return res
    },
    async updateCategory({ commit }, data) {
        console.log(data)
        const res = await commentService.update(data)
        commit("PUT_CATEGORY", res.data)
        console.log(res)
        return res
    },
    /* eslint-disable */
    async deleteCategory({ commit }, data) {
        const res = await commentService.delete(data.id)
        // commit("DELETE_CATEGORY", data)
        return res
    }
};
