import axios from 'axios'
import env from "../../env";

export default class Http {

  constructor(status) {
    this.handlerEnabled =
      status && status.handlerEnabled ? status.handlerEnabled : false;
    this.instance = axios.create({
      baseURL: env.API_URL,
    });
    return this.init();
  }

  async requestHandler(request) {
    const store = require("../store");
    console.log('store')
    console.log(store.default.state)
    // console.log(store.default.replaceState)
    const token = localStorage.getItem('token')
    console.log(token)
    console.log(request.url)
    const authenticated = !request.url.startsWith("oauth2/login");
    console.log(authenticated)
    if(token && authenticated){
      const res = await axios.post('http://127.0.0.1:8000/api/token/verify/', {'token': token})
      if(res.status == 200){
        request.headers["Authorization"] = `Bearer ${token}`
      }
      if(res.status == 401){
        console.log('ok')
        localStorage.removeItem('token');
      }
    }
    return request;
  }

  errorHandler(error) {
    if (error?.response?.status === 401) {
      return error.response
    }
    console.log(error)
    return Promise.reject(error);
  }

  successHandler(response) {
    if (this.handlerEnabled) {
      return response; // TODO: Handle Success Response if need
    }
    return response;
  }

  init() {
    this.instance.interceptors.request.use((request) =>
      this.requestHandler(request)
    );
    this.instance.interceptors.response.use(
      (response) => this.successHandler(response),
      (error) => this.errorHandler(error)
    );
    return this.instance;
  }
}
