export default {
  GET_COMMENT(state, data) {
    state.category = data
  },
  POST_COMMENT(state, data) {
    state.category.unshift(data)
  },
  PUT_COMMENT(state, data) {
    state.category.forEach(element => {
      if(element.id === data.id){
        element.name = data.name;
        return 0;
      }
    });
  },
};
